mapper.update:
UPDATE GASMEMBER SET EMAIL=#{email}, PHONE=#{phone}
 
 	,NAME=#{name}, ROLE=#{role} 
 
 WHERE UPPER(ID)=UPPER(#{id})

mapper.select:
SELECT SEQ,ID,TITLE,CONTENT,REGDATE,REFER,STEP,DEPTH,READCOUNT,LIKECOUNT FROM (SELECT ROWNUM AS RN , SEQ,ID,TITLE,CONTENT,REGDATE,REFER,STEP,DEPTH,READCOUNT,LIKECOUNT FROM (SELECT * FROM GASFREEBOARD ORDER BY REFER DESC,STEP ASC)
WHERE STEP=0 AND ID LIKE '%'||#{index}||'%'
WHERE STEP=0 AND TITLE LIKE '%'||#{index}||'%'
WHERE STEP=0 AND CONTENT LIKE '%'||#{index}||'%'
) WHERE RN BETWEEN #{min} AND #{max}
